Actionscript.script:
import flash.events.Event;
import flash.events.KeyboardEvent;

var World1:world_ = new world_;
var player:dude_ = new dude_;

stage.addChild(World1);
stage.addChild(player);

stage.addEventListener(KeyboardEvent.KEY_DOWN, startSpin);
stage.addEventListener(KeyboardEvent.KEY_UP, stopSpin) 
stage.addEventListener(Event.ENTER_FRAME, monitorKeyboardState);

var initialize = false;
if(initialize == false){ Initialize();}

function Initialize():void{
	World1.visible = true;
	World1.x = 400;
	World1.y = 300;
	World1.rotationSpeed = 1;
	World1.leftDown = false;
	World1.rightDown = false;

	player.visible = true;
	player.x = 400;
	player.y = 135;
	player.ang = 0; // angle
	player.angleSpeed = 1;
	initialize = true;
}



function monitorKeyboardState(e:Event):void {
	if(World1.leftDown == true){
		//player.ang -= player.angleSpeed;
		//if(player.ang <=0){ player.ang +=360;}
		//player.x = (Math.sin(AngleToRadians(player.ang)) * 200) + 400;
		//player.y = (Math.cos(AngleToRadians(player.ang)) * 200) + 135;
		World1.rotation -=World1.rotationSpeed;
	}
	if(World1.rightDown == true){
		World1.rotation +=World1.rotationSpeed;
	}
}

function stopSpin(e:KeyboardEvent):void{
	if(e.keyCode == 39) {
		World1.leftDown = false;		
	}
	if(e.keyCode == 37) {
		World1.rightDown = false;		
	}
}

function startSpin(e:KeyboardEvent):void{
	if(e.keyCode == 39) {
		World1.leftDown = true;		
	}
	if(e.keyCode == 37) {
		World1.rightDown = true;		
	}
}

