// player attributes
var playerAttributes = {
	rotationSpeed:2,
	leftDown:false,
	rightDown:false,
	ang:180,
	angleSpeed:2
};

// world attributes
var worldAttributes = {
	mass:1,
	leftDown:false,
	rightDown:false,
	maxRotationSpeed:2,
	rotationSpeed:0
};

import flash.events.Event;
import flash.events.KeyboardEvent;

var player:dude_ = new dude_;
player.playerAttributes = playerAttributes;
trace(player.playerAttributes.rotationSpeed);

stage.addChild(player);

stage.addEventListener(KeyboardEvent.KEY_DOWN, startPlayerSpin);
stage.addEventListener(KeyboardEvent.KEY_UP, stopPlayerSpin) 
stage.addEventListener(Event.ENTER_FRAME, monitorKeyboardState);


player.visible = true;
player.x = 400;
player.y = 135;
/*
player.rotationSpeed = 1;
player.leftDown = false;
player.rightDown = false;
player.ang = 180; // angle
player.angleSpeed = 1;
*/
function monitorKeyboardState(e:Event):void {
	if(player.playerAttributes.leftDown == true){
		player.playerAttributes.ang -= player.playerAttributes.angleSpeed;
		if(player.playerAttributes.ang <=0){ player.playerAttributes.ang +=360;}
		player.x = (Math.sin(AngleToRadians(player.playerAttributes.ang)) * 163) + 400;
		player.y = (Math.cos(AngleToRadians(player.playerAttributes.ang)) * 163) + 300;
		player.rotation +=player.playerAttributes.rotationSpeed;
	}
	if(player.playerAttributes.rightDown == true){
		player.playerAttributes.ang += player.playerAttributes.angleSpeed;
		if(player.playerAttributes.ang >=360){ player.playerAttributes.ang -=360;}
		player.x = (Math.sin(AngleToRadians(player.playerAttributes.ang)) * 163) + 400;
		player.y = (Math.cos(AngleToRadians(player.playerAttributes.ang)) * 163) + 300;
		player.rotation -=player.playerAttributes.rotationSpeed;
	}
}

function stopPlayerSpin(e:KeyboardEvent):void{
	if(e.keyCode == 39) {
		player.playerAttributes.leftDown = false;		
	}
	if(e.keyCode == 37) {
		player.playerAttributes.rightDown = false;		
	}
}

function startPlayerSpin(e:KeyboardEvent):void{
	if(e.keyCode == 39) {
		player.playerAttributes.leftDown = true;		
	}
	if(e.keyCode == 37) {
		player.playerAttributes.rightDown = true;		
	}
}

function AngleToRadians( angle ){
	var returnAngle = Math.PI * angle / 180;
	return returnAngle;
}

import flash.events.Event;
import flash.events.KeyboardEvent;

var World1:world_ = new world_;

stage.addChild(World1);

stage.addEventListener(KeyboardEvent.KEY_DOWN, startWorldSpin);
stage.addEventListener(KeyboardEvent.KEY_UP, stopWorldSpin);
stage.addEventListener(Event.ENTER_FRAME, monitorWorldKeyboardState);

World1.x = 400;
World1.y = 300;

World1.worldAttributes.rotationSpeed = 0;
World1.worldAttributes.maxRotationSpeed = 2;

World1.worldAttributes.leftDown = false;
World1.worldAttributes.rightDown = false;

World1.worldAttributes.mass = 5000;

function monitorWorldKeyboardState(e:Event):void {
	if (World1.leftDown == false && World1.rightDown == false)
	{
		if(World1.rotationSpeed < 0)
		World1.rotationSpeed += (50 / World1.mass);
		else if (World1.rotationSpeed > 0)
		World1.rotationSpeed -= (50 / World1.mass);
	}
	else if (
			World1.rotationSpeed < World1.maxRotationSpeed && 
			World1.rotationSpeed > -World1.maxRotationSpeed)
	{
		if(World1.leftDown == true)
		{
			World1.rotationSpeed -= (50 / World1.mass);
		}
		if(World1.rightDown == true)
		{
			World1.rotationSpeed += (50 / World1.mass);
		}
	}
	World1.rotation += World1.rotationSpeed;
}

function stopWorldSpin(e:KeyboardEvent):void{
	if(e.keyCode == 39) {
		World1.leftDown = false;		
	}
	if(e.keyCode == 37) {
		World1.rightDown = false;		
	}
}

function startWorldSpin(e:KeyboardEvent):void{
	if(e.keyCode == 39) {
		World1.leftDown = true;		
	}
	if(e.keyCode == 37) {
		World1.rightDown = true;		
	}
}
